mapper.select:
select c.user, c.price, c.time, c.status,
  s.id as sid, s.orderInfo as sorderInfo, s.commodity as scommodity,
  s.receive as sreceive, s.commodityType as scommodityType,
  s.count as scount, s.time as stime
from OrderInfo c, Orderdetails s
WHERE c.status = #{status} AND c.id in (
  SELECT orderInfo FROM OrderDetails WHERE commodity IN (
    select id FROM Commodity WHERE shop = #{shopId}
  )
)ORDER BY c.id DESC LIMIT #{beginRow}, #{pageSize};
